package javatest;

public class Ex3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char start = 'A';
        char end = 'Z';

        System.out.println("Characters from A to Z:");

        for (char i = start; i <= end; i++) {
            System.out.print(i + " ");
        }

        System.out.println(); // Print a newline after the loop

	}

}
